import React, { useState } from 'react';


export const Homepage = () => {

    const Card = ({ make, model, year, odometer }) => {
        return (
          <div className="card">
            <h2>{make} - {model}</h2>
            <p>Year: {year}</p>
            <p>Odometer: {odometer}</p>
          </div>
        );
      };


      const PopUpForm = ({ onSubmit }) => {
        const [make, setMake] = useState('');
        const [model, setModel] = useState('');
        const [year, setYear] = useState('');
        const [odometer, setOdometer] = useState('');
      
        const handleSubmit = (e) => {
          e.preventDefault();
          onSubmit({ make, model, year, odometer });
          setMake('');
          setModel('');
          setYear('');
          setOdometer('');
        };
      
        return (
          <div className="popup">
            <form onSubmit={handleSubmit}>
              <label htmlFor="make">Make:</label>
              <input
                type="text"
                id="make"
                value={make}
                onChange={(e) => setMake(e.target.value)}
                required
              />
              <label htmlFor="model">Model:</label>
              <input
                type="text"
                id="model"
                value={model}
                onChange={(e) => setModel(e.target.value)}
                required
              />
              <label htmlFor="year">Year:</label>
              <input
                type="number"
                id="year"
                value={year}
                onChange={(e) => setYear(e.target.value)}
                required
              />
              <label htmlFor="odometer">Current Odometer:</label>
              <input
                type="number"
                id="odometer"
                value={odometer}
                onChange={(e) => setOdometer(e.target.value)}
                required
              />
              <button type="submit">Submit</button>
            </form>
          </div>
        );
      };
    

      const [cards, setCards] = useState([]);
  const [showPopUp, setShowPopUp] = useState(false);

  const handleAddCard = (newCard) => {
    setCards([...cards, newCard]);
    setShowPopUp(false);
  };

  return (
    <div className="app">
      <div className="header">
        <button onClick={() => setShowPopUp(true)}>Add Card</button>
      </div>
      <div className="card-grid">
        {cards.map((card, index) => (
          <Card
            key={index}
            make={card.make}
            model={card.model}
            year={card.year}
            odometer={card.odometer}
          />
        ))}
      </div>
      {showPopUp && <PopUpForm onSubmit={handleAddCard} />}
    </div>
  );
};
