    // return (
    //     <div class="cards">
    //         <h1>Cards</h1>
    //         {cards.map((card, i) => (
    //             <div key={i}>
    //                 <h2>{card.title}</h2>
    //                 <textarea value={card.content} onChange={(e) => setCards(cards.map((c, i) => i === c.id ? { ...c, content: e.target.value } : c))} />
    //                 <input class="pog" value={card.inputField} onChange={(e) => setCards(cards.map((c) => i === c.id ? { ...c, inputField: e.target.value } : c))} />
    //                 <button onClick={() => deleteCard(i)}>Delete</button>
    //             </div>
    //         ))}
    //         <button onClick={addCard}>Add Card</button>
    //     </div>
    // )
    const [open, setOpen] = useState(false);
    const [cardData, setCardData] = useState({});
  
    const handleOpen = () => {
      setOpen(true);
    };
  
    const handleClose = () => {
      setOpen(false);
    };
  
    const handleSubmit = (e) => {
      e.preventDefault();
      // do something with the card data
      setOpen(false);
    };
  
    return (
      <>
      <div class="cards">
        <button onClick={handleOpen}>Open Modal</button>
        <div
          style={{
            display: open ? "block" : "none",
            padding: "20px",
            background: "white",
            border: "1px solid black",
          }}
        >
          <h1>Card Modal</h1>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Enter card name"
              value={cardData.name}
              onChange={(e) => setCardData({ ...cardData, name: e.target.value })}
            />
            <input
              type="text"
              name="description"
              placeholder="Enter card description"
              value={cardData.description}
              onChange={(e) => setCardData({ ...cardData, description: e.target.value })}
            />
            <button type="submit">Submit</button>
          </form>
        </div>
        </div>
      </>
    );
}
