import React, { useEffect, useState } from 'react';
import { useParams } from "react-router-dom";

export const VehicleId = () => {
    console.log(useParams())
    const { vehicleid } = useParams();
    var make,model,year,odometer,service_history;

    useEffect(() => {
        let ignore = false;

        async function startFetching() {

            const data = {
                vehicle_id: vehicleid
            };
            const headers = {
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin':'*',
                'Access-Control-Allow-Methods':'POST,PATCH,OPTIONS'
            };
            fetch("http://localhost:5000/get_vehicle", {
                method: 'POST',
                headers: headers,
                body: JSON.stringify(data),
            })
            .then((response)=>{
                response.json().then(function(result){
                    if (!ignore) {
                        console.log(result["vehicle"])
                        make = result["vehicle"][1]
                        model = result["vehicle"][2]
                        year = result["vehicle"][3]
                        odometer = result["vehicle"][4]
                        service_history = result["vehicle"][5]
                    }
                })
            })
            .then((data) => {
                console.log('Success:', data);

            })
            .catch((error) => {
                console.error('Error:', error);
            });
        }

        startFetching();

        return () => {
            ignore = true;
        };
      },[]);

    console.log(make);
    return (
        <div id="root">
            <h1>Vehicle Information</h1>
            <p><strong>Make:</strong>{make}</p>
            <p><strong>Model:</strong>{model}</p>
            <p><strong>Year:</strong>{year}</p>
            <p><strong>Odometer Reading:</strong>{odometer} miles</p>
            <span><button id="updateHistoryBtn">Update Service History</button><button id="NotifBtn">Send Service Reminder</button></span>

            <div id="serviceHistoryModal" style={{display: "none"}}>
            <h2>Update Service History</h2>
            <textarea id="historyInput" rows="5" cols="50"></textarea>
            <button id="saveHistoryBtn">Save</button>
            <button id="cancelHistoryBtn">Cancel</button>
            </div>

            <h2>Service History & Notes</h2>
            <textarea id="service_history" defaultValue={service_history} name="service_history" rows="4" cols="50"></textarea>
        </div>
    );
}
